mapper.select:
select b.*, s.id as sampleId, s.qymcCn sampleName, s.ssqx as areaCode from approve_gxqy b, `user` u, sample s

    u.id = b.userId and u.id = s.userId

        and u.areaCode = #{areaCode}


        and u.id = #{userId}



        and (s.qymcCn like #{temp} or s.qymcEn like #{temp})


        and b.status = #{status}


order by id desc limit #{start}, #{pageSize}

select count(*) from approve_gxqy b, `user` u, sample s

    u.id = b.userId and u.id = s.userId

        and u.areaCode = #{areaCode}


        and u.id = #{userId}



        and (s.qymcCn like #{temp} or s.qymcEn like #{temp})


        and b.status = #{status}
