mapper.select:
select p.* from pdf p, sample s, `user` u

    p.userId = u.id and s.userId = u.id


        and (s.qymcCn like #{sampleNameTemp} or s.qymcEn like #{sampleNameTemp})


        and p.status = #{status}


        and s.ssqx = #{areaCode}


        and u.id = #{userId}


order by id desc limit #{start}, #{pageSize}

select count(*) from pdf p, sample s, `user` u

    p.userId = u.id and s.userId = u.id


        and (s.qymcCn like #{sampleNameTemp} or s.qymcEn like #{sampleNameTemp})


        and p.status = #{status}


        and s.ssqx = #{areaCode}


        and u.id = #{userId}
